import './NewExpense.css'
import React,{useState} from 'react';
function NewExpense1(props){
    const[enteredTitle,setEnteredTitle]=useState('');
    function titleChangeHandler(event){
        setEnteredTitle(event.target.value);

    }
    const[enteredAmount,setEnteredAmount]=useState('');
    function amountChangeHandler(event){
        setEnteredAmount(event.target.value);

    }
    const[enteredDate,setEnteredDate]=useState('');
    function dateChangeHandler(event){
        setEnteredDate(event.target.value);

    }
    function saveExpenseDataHandler(enteredExpenseData){
        const expenseData={
            ...enteredExpenseData,
            id: Math.random.toString()
        }
    }
    function submitHandler(event){
        event.preventDefault();
        const expenseData={
            title:enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        };
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
        props.onSaveExpense(expenseData);

    }
    return (<div className='new-expense'>
        <form onSubmit={submitHandler}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" value={enteredTitle} onChange={titleChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" value={enteredAmount} onChange={amountChangeHandler}></input>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" max="2023-12-31" value={enteredDate} onChange={dateChangeHandler}></input>
                </div>
                
            </div>
            <div className='new-expense__actions'>
                    <button className='sbmtbtn'>Add Expense</button>
                </div>
        </form>
    </div>)

};
export default NewExpense1;
