import ExpenseItem from "./ExpenseItem";
import { useState } from "react";
import "./Expenses.css"
import ExpensesFilter from "./ExpensesFilter";
function Expenses(props) {
  const[year,setYear]=useState('2020');
  const[filterInfoText,setfilterInfoText]=useState('2019,2021 & 2022')
  function FilterChangeHandler(selectedYear){
    setYear(selectedYear);
    if(selectedYear==2019){
      setfilterInfoText('2020,2021 & 2022');
    }
    else if(selectedYear==2020){
      setfilterInfoText('2019,2021 & 2022');
    }
    else if(selectedYear==2021){
      setfilterInfoText('2019,2020 & 2022');
    }
    else{
      setfilterInfoText('2019,2020 & 2021')
    }

  }
  return (
    <div className="expenses">
      <ExpensesFilter onSelected={year} onChangeYear={FilterChangeHandler}/>
      <p className="ptext">the data of years {filterInfoText} is hidden</p>
      {props.items.map(expense => <ExpenseItem 
        title={expense.title}
        amount={expense.amount}
        date={expense.date}></ExpenseItem>)}
      
    </div>
  );
}
export default Expenses;
